    }

    getApiData(url, apiKey) {
        const fullUrl = url + '&api_key=' + apiKey;
        console.log(fullUrl);
        fetch(fullUrl)
        .then(res => {
            const cryptoData = res.Data;
            console.log(cryptoData);
            this.setState({
                isLoaded: true,
                coinList: cryptoData
            })
        })
        console.log(this.state.coinList);
    }



    render() {
        if (this.state.isLoaded) {
            return (
                <div>
                    <p>Loaded</p>
                </div>
            )
        } else {
            return (
                <div>
                    <p>loading</p>
                </div>
            )
        }
    }

