import React, { Component } from 'react'
import DisplayCoins from './DisplayCoins'
import LoadingScreen from './LoadingScreen'
import axios from 'axios';
export default class Market extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoaded: false,
            coinList: null,
        }
        this.getApiData = this.getApiData.bind(this);
    }


//


    componentDidMount() {
        this.getApiData();
        // setInterval(this.getApiData, 100000);


    }

    getApiData() {
        const url = 'https://min-api.cryptocompare.com/data/top/mktcapfull?limit=10&tsym=USD';
        const apiKey = 'aff46362b8aef9ef13b86e6eb6c5e55bd01e664ed9cef2b0dffd71d37ae9add0';
        const fullURL = url + '&api_key=' + apiKey;
        let coinData = null;
        axios.get(fullURL)
            .then(res => {
                console.log(res.data.Data);
                this.setState({
                    isLoaded: true,
                    coinData: res.data.Data,

                })
            })





    }
    getCoinList(coinData) {
        const coins = coinData.map((coin) => {
            return {
                name: coin.CoinInfo.Name,
                fullName: coin.CoinInfo.FullName,
                price: coin.DISPLAY.USD.PRICE,
            }
        })
        return coins;
    }
    render() {
        let coin_list = null;
        if (this.state.isLoaded) {
            coin_list = this.getCoinList(this.state.coinList);

        const display_coins = coin_list.map(coin => {
            return (
                <div className="crypto-conatiner">
                    <span className="left">{coin.name}</span>
                    <span className="right">{coin.price}</span>
                </div>
            )
        })
        }
        if(this.state.isLoaded) {
            return(
                <div>
                    {display_coins}
                </div>
            )
        }
    }

