import React from 'react'


const DisplayCoins = ({coinData}) => {
    const cryptoCompareURL = 'https://www.cryptocompare.com';
    const MAX_CHAR_LIM = 15;
    const coins = coinData.map((coin) => {
        const imageURL = cryptoCompareURL + coin.CoinInfo.ImageUrl;
        return {
            ticker: coin.CoinInfo.Name,
            fullName: coin.CoinInfo.FullName,
            price: coin.DISPLAY.USD.PRICE,
            image: imageURL,
            percentHourChange: coin.DISPLAY.USD.CHANGEPCTHOUR,
        }
    })
    console.log(coins);
    const getFormattedFullName = (fullName) =>{
        if (fullName.length > 15) {
            return fullName.slice(0, 11) + "...";
        } else {
            return fullName;
        }
    }

    const getPercentChangePerHour = (percent) => {
        console.log(percent);
        if (percent == 0){
            return (
                <div id="no-change">{percent}% Change Today</div>
            )
        } else if (percent > 0) {
            return (
                <div id="positive-change">Change % (hr){percent}</div>
            )
        } else {
            return (
                <div id="negative-change">Change %(hr){percent}</div>
            )
        }
    }

    const coinList = coins.map(coin => {
        return (
            <div className="container services" key={coin.ticker}>

                    <div className="col s3 center-align" >
                        <div className="card">
                            <div className="card-content">
                                <img src={coin.image} alt={coin.ticker} width="40" height="40"/>

                                <h5>{coin.ticker}</h5>
                                <p>{getFormattedFullName(coin.fullName)}</p>
                                <div>{getPercentChangePerHour(coin.percentHourChange)}</div>
                                <h6>{coin.price}</h6>
                            </div>
                            <div className="card-action"><a href="#">Add...</a></div>

                        </div>
                    </div>
            </div>








        )




    })










    console.log(coins);


    return (
        <div className="market">
            <div className="row">
                {coinList}
            </div>
        </div>
    )
}


export default DisplayCoins;
