import React, { Component } from 'react'
import DisplayCoins from './DisplayCoins'
import LoadingScreen from './LoadingScreen'
import cryptocompare from 'cryptocompare';

export default class Market extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoaded: false,
            coinList: null,
        }
    }





    componentDidMount() {
        const url = 'https://min-api.cryptocompare.com/data/top/mktcapfull?limit=10&tsym=USD';
        const apiKey = 'aff46362b8aef9ef13b86e6eb6c5e55bd01e664ed9cef2b0dffd71d37ae9add0';
        this.getApiData(url, apiKey);

        // const cc = require('cryptocompare');
        // cc.setApiKey('API KEY GOES HERE')
        // cc.coinList().then(coinList => {
        //     console.log(coinList);
        // })
        // // setInterval(this.fetchResults, 5000);

    }

    getApiData(url, apiKey) {
        const fullUrl = url + '&api_key=' + apiKey;
        fetch(fullUrl)
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                coinList: json.Data,
            })
        })
        console.log("hello");
        console.log(this.state.coinList);
    }

    render() {
        return (
            <div>
                <div>
                    {!this.state.coinList ? (
                    <LoadingScreen />
                     ) : ( 
                    <DisplayCoins coinData={this.state.coinList}/>
                     )}
                </div>
            </div>
        )
    }
}
