import React, { Component } from 'react'
import DisplayCoins from './DisplayCoins'
export default class Market extends Component {
    state = {
        loading: true,
        coinData: null,
    }
    async componentDidMount() {
        const url = "https://min-api.cryptocompare.com/data/top/mktcapfull?limit=10&tsym=USD";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({name: data.Data[0].CoinInfo.name});
        this.setState({name: data.Data[0].DISPLAY.price})

        console.log(data.Data);
    }

    render() {
        return (
            <div>
                <div>
                    {this.state.loading || !this.state.coinData ? (
                     <div>LOADING... (ADD LOADING GIF HERE)</div>
                     ) : ( 
                    <DisplayCoins coinData={this.state.coinData}/>
                     )}
                </div>
            </div>
        )
    }
}
